// OpenOnErrorFileStream is a bunyan stream that only creates the file when
// there's an error or higher level message or when the global log_to_file
// variable is set. For actions that modify things log_to_file is always set.
// For other actions we shouldn't log in the normal case but where we do want
// logs when something breaks. Thanks to Trent++ for most of this code.
//
// Note: if you want to rotate the logs while this is writing to a file, you
// can first move it. The watcher will notice that the log file was moved and
// reopen a new file with the original name.

function OpenOnErrorFileStream(filename) {
    this.path = filename;
    this.write = this.constructor.prototype.write1;
    this.end = this.constructor.prototype.end1;
    this.on = this.constructor.prototype.on1;
    this.once = this.constructor.prototype.once1;
    this.emit = this.constructor.prototype.emit1;

    this.newStream = function () {
        var self = this;
        var watcher;

        self.stream = fs.createWriteStream(self.path,
            {flags: 'a', encoding: 'utf8'});

        watcher = fs.watch(self.path, {persistent: false}, function (evt) {
            if (evt != 'rename') {
                return;
            }
            // file was renamed, we want to reopen.
            if (self.stream) {
                self.stream.destroySoon();
            }
            watcher.close();
            self.stream = null;
        });
    };
}

OpenOnErrorFileStream.prototype.end1 = function () {
    // in initial mode we're not writing anything, so nothing to flush
    return;
};

OpenOnErrorFileStream.prototype.on1 = function (name, cb) {
    return;
};

OpenOnErrorFileStream.prototype.once1 = function (name, cb) {
    return;
};

OpenOnErrorFileStream.prototype.emit1 = function (name, cb) {
    return;
};

// used until first ERROR or higher, then opens file and ensures future writes
// go to .write2()
OpenOnErrorFileStream.prototype.write1 = function (rec) {
    var r;

    if (rec.level >= bunyan.ERROR || log_to_file) {
        if (! this.stream) {
            this.newStream();
        }
        this.end = this.stream.end;
        this.emit = this.stream.emit;
        this.on = this.stream.on;
        this.once = this.stream.once;
        this.write = this.constructor.prototype.write2;
        // dump out logs from ringbuffer too since there was an error so we can
        // figure out what's going on.
        for (r in ringbuffer.records) {
            r = ringbuffer.records[r];
            if (r != rec) {
                this.write(r);
            }
        }

        this.write(rec);
    }
};

// used when writing to file
OpenOnErrorFileStream.prototype.write2 = function (rec) {
    var str;

    // won't write an empty message to the file
    if (typeof (rec) === 'string' && rec.length < 1) {
        return;
    }

    str = JSON.stringify(rec, bunyan.safeCycles()) + '\n';

    if (! this.stream) {
        this.newStream();
    }

    this.stream.write(str);
};

// This function should be called by any exported function from this module.
// It ensures that a logger is setup. If side_effects is true, we'll start
// writing log messages to the file right away. If not, we'll only start
// logging after we hit a message error or higher. This is intended such that
// things that are expected to change the state or modify VMs on the system:
// eg. create, start, stop, delete should have this set true.  It should be
// set false when the action should not cause changes to the system:
// eg.: load, lookup, info, console, &c.
function ensureLogging(side_effects)
{
    side_effects = !!side_effects; // make it boolean (undef === false)

    var filename;
    var logname;
    var streams = [];

    function start_logging() {
        VM.log = bunyan.createLogger({
            name: logname,
            streams: streams,
            serializers: bunyan.stdSerializers
        });
    }

    // This is here in case an app calls a lookup first and then a create.  The
    // logger will get created in no-sideeffects mode for the lookup but when
    // the create is called this will force the switch to writing.
    if (side_effects) {
        log_to_file = true;
    }

    if (VM.log) {
        // We're already logging, don't break things.
        return;
    }

    if (VM.hasOwnProperty('logname')) {
        logname = VM.logname.replace(/[^a-zA-Z\_]/g, '');
    }
    if (!logname || logname.length < 1) {
        logname = 'VM';
    }

    if (VM.hasOwnProperty('logger') && VM.logger) {
        // Use concat, in case someone's sneaky and makes more than one logger.
        // We don't officially support that yet though.
        streams = streams.concat(VM.logger);
    }

    // Add the ringbuffer which we'll dump if we switch from not writing to
    // writing, and so that they'll show up in dumps.
    streams.push({
        level: 'trace',
        type: 'raw',
        stream: ringbuffer
    });

    try {
        if (!fs.existsSync('/var/log/vm')) {
            fs.mkdirSync('/var/log/vm');
        }
    } catch (e) {
        // We can't ever log to a file in /var/log/vm if we can't create it,
        // so we just log to ring buffer (above).
        start_logging();
        return;
    }

    filename = '/var/log/vm/' + Date.now(0) + '-'
        + sprintf('%06d', process.pid) + '-' + logname + '.log';

    streams.push({
        type: 'raw',
        stream: new OpenOnErrorFileStream(filename),
        level: VM.loglevel
    });

    start_logging();
}

function ltrim(str, chars)
{
    chars = chars || '\\s';
    str = str || '';
    return str.replace(new RegExp('^[' + chars + ']+', 'g'), '');
}

function rtrim(str, chars)
{
    chars = chars || '\\s';
    str = str || '';
    return str.replace(new RegExp('[' + chars + ']+$', 'g'), '');
}

function trim(str, chars)
{
    return ltrim(rtrim(str, chars), chars);
}

function isUUID(str) {
    var re = /^[a-f0-9]{8}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{4}-[a-f0-9]{12}$/;
    if (str.length === 36 && str.match(re)) {
        return true;
    } else {
        return false;
    }
}

function fixBoolean(str)
{
    if (str === 'true') {
        return true;
    } else if (str === 'false') {
        return false;
    } else {
        return str;
    }
}

function fixBooleanLoose(str)
{
    if (str === 'true' || str === '1' || str === 1) {
        return true;
    } else if (str === 'false' || str === '0' || str === 0) {
        return false;
    } else {
        return str;
    }
}

